package git.io.join.config;import com.zaxxer.hikari.HikariDataSource;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import javax.sql.DataSource;@Configurationpublic class DatasourceConfig {    private final String driverClassName;    private final String url;    private final String username;    private final String password;    public DatasourceConfig(            @Value("${spring.datasource.driver-class-name}") String driverClassName,            @Value("${spring.datasource.url}") String url,            @Value("${spring.datasource.username}") String username,            @Value("${spring.datasource.password}") String password,            Encrypt decode) {        this.driverClassName = driverClassName;        this.url = decode.decryptAES256(url);        this.username = decode.decryptAES256(username);        this.password = decode.decryptAES256(password);    }    @Bean    public DataSource dataSource() {        return new HikariDataSource() {{            setDriverClassName(driverClassName);            setJdbcUrl(url);            setUsername(username);            setPassword(password);        }};    }}